&lt;h4 id=&#34;统一导出&#34;&gt;&lt;a class=&#34;卓越科技-&#34; href=&#34;#统一导出&#34;&gt;#&lt;/a&gt; 统一导出&lt;/h4&gt;
&lt;pre&gt;&lt;code&gt;var age = 22
function sum(num1, num2) &amp;#123;
  return num1 + num2
&amp;#125;
var flag = true
if (flag) &amp;#123;
  console.log(sum(10, 20))
&amp;#125;
//2.最后统一导出
export &amp;#123;
  flag,sum,age
&amp;#125;
