&lt;figure class=&#34;highlight css&#34;&gt;&lt;figcaption data-lang=&#34;CSS&#34;&gt;&lt;/figcaption&gt;&lt;table&gt;&lt;tr&gt;&lt;td data-num=&#34;1&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;&lt;span class=&#34;token property&#34;&gt;-webkit-clip-path&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;:&lt;/span&gt; &lt;span class=&#34;token function&#34;&gt;polygon&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;(&lt;/span&gt;50% 0%&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; 90% 20%&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; 100% 60%&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; 75% 100%&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; 25% 100%&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; 0% 60%&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; 10% 20%&lt;span class=&#34;token punctuation&#34;&gt;)&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;;&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;&lt;/li&gt;
